import React from "react";
import {useState} from "react";
import "./Gallery.css"; // CSS elements
import Lightbox from "yet-another-react-lightbox"; // Lightbox extension for slideshow capabilites of portfolio photos
import "yet-another-react-lightbox/styles.css";
import Counter from "yet-another-react-lightbox/plugins/counter"; // Photo counter for added information on the position of the lightbox
import "yet-another-react-lightbox/plugins/counter.css";
import { SocialIcon } from 'react-social-icons'; // Social Icons extensions to display clickable external social profiles
import { photos } from './photos';  // Import the photos array

function Gallery() {

  const [Open, setOpen] = useState(false);
  const [imageToShow, setImageToShow] = useState('')
  
  const showImage = (photo) => {
    setImageToShow(photo);
    setOpen(true);
  };
  return (
    <div>
      <header className="Gallery-header">
        <h1>GALLERY</h1>
      </header>
      <div className="Quote">
        <body>“A good photograph is knowing where to stand.”</body>
      </div>
      <header className="h2">
        - Ansel Adams 
      </header>
      <div className="Desc">
        <body>(American Photographer & Environmentalist
        )</body>
      </div>
      <div className="Body">
        <body>Click on an image to expand slideshow.</body>
      </div>
      
      <main>
      
      <div className="gallery">
          {photos.map((photo, index) => (
            <img
              onClick={() => showImage(photo)}
              key={index}
              src={photo.src}
              alt={photo.alt}
              className="gallery-photo"
            />
          ))}
        </div>

      <Lightbox
        open={Open}
        close={() => setOpen(false)}
        slides={photos}
        plugins={[Counter]}
      />
      <footer className="Links">
      <h1>Social Links</h1>
        <SocialIcon
          network="linkedin" 
          url="https://www.linkedin.com/in/rui-fernandes-567409171/" 
          style={{ height: 75, width: 75 }} 
        />
        &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
        <SocialIcon 
          network="github" 
          url="https://github.com/RuiSantinoFernandes" 
          style={{ height: 75, width: 75 }}
        />
      </footer>
      </main>
    </div>
  );
}

export default Gallery;
